import React, { Component } from "react";
import moment from "moment";
import { Field, Form, Formik } from "formik";

class JobComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      jobTitle: "Super_Hero",
      company: "UA",
      webLink: "UA.com",
      status: "interviewed",
      appliedDate: moment(new Date()).format("YYYY-MM-DD"),
      notes: "go beyond",
    }

    this.onSubmit = this.onSubmit.bind(this)
  }

  onSubmit(values){
      console.log(values)
  }
  render() {
    let jobTitle = this.state.jobTitle;
    let company = this.state.company;
    let webLink = this.state.webLink;
    let status = this.state.status;
    let appliedDate = this.state.appliedDate;
    let notes = this.state.notes;

    return (
      <div>
        <h1>Jobs</h1>
        <div className="container">
          <Formik
            initialValues={{jobTitle,company,webLink,status,appliedDate,
              notes,
            }}
            onSubmit={this.onSubmit}
          >
            {(props) => (
              <Form>
                <fieldset className="form-group">
                  <label>Job Title</label>
                  <Field className="form-control" type="text" name="jobTitle" />
                </fieldset>
                <fieldset className="form-group">
                  <label>Company</label>
                  <Field className="form-control" type="text" name="company" />
                </fieldset>
                <fieldset className="form-group">
                  <label>Link</label>
                  <Field className="form-control" type="text" name="webLink" />
                </fieldset>
                <fieldset className="form-group">
                  <label>Status</label>
                  <Field className="form-control" type="text" name="status" />
                </fieldset>
                <fieldset className="form-group">
                  <label> Date</label>
                  <Field
                    className="form-control"
                    type="date"
                    name="targetDate"
                  />
                </fieldset>
                <fieldset className="form-group">
                  <label>Notes</label>
                  <Field className="form-control" type="text" name="notes" />
                </fieldset>
                <button className="btn btn-success" type="submit">
                  Submit
                </button>
              </Form>
            )}
          </Formik>
        </div>
      </div>
    );
  }
}

export default JobComponent;
