import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'

class JobTrackerApp extends Component {
    render(){
        return (
            <div className="JobTrackerApp">
                <Router>
                   <Switch>
                        <Route path="/"exact component={LoginComponent} />
                        <Route path="/login" component={LoginComponent} />
                        <Route path="/welcome/:name" component={WelcomeComponent} />
                        <Route path="/jobs" component={ListApplicationComponent}></Route>
                        <Route  component={ErrorComponent} />
                    </Switch>
                </Router>


            </div>
        )
    }
}

class ListApplicationComponent extends Component{
    co

    render(){
        return <div>
                    <h1>Applications</h1>
                    <table>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Description</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>{this.state.job.id}</td>
                                <td>{this.state.job.description}</td>
                            </tr>
                            </tbody>
                    </table>
            </div>
    }
}

class WelcomeComponent extends Component{
    render(){
    return <div>Welcome {this.props.match.params.name}, To Job Application Tracker</div>
    }

}

function ErrorComponent(){
    return <div>I think you are lost buddy </div>
}

class LoginComponent extends Component{

    constructor(props){
        super(props)
        this.state = {
            username: 'jobTracker',
            password: '',
            hasLoginFailed: false,
            showSuccessfulMessage: false
        }


        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    handleChange(event) {
        this.setState({
            [event.target.name]
                : event.target.value
        })
    }

    loginClicked(){
        //admin,password
        if(this.state.username === 'icandoit' && this.state.password ==='yesican'){
            this.props.history.push(`/welcome/${this.state.username}`)
            console.log('Successful')

        }
        else {
            console.log('Failed')
            this.setState({showSuccessfulMessage: false})
            this.setState({hasLoginFailed: true})
        }


    }

    render(){
        return (

            <div>   

                {this.state.showSuccessfulMessage && <div>Login Successful</div>}
                {this.state.hasLoginFailed && <div>Invalid Credentials</div> }
                UserName: <input type="text" name="username" value= {this.state.username} onChange={this.handleChange} />
                Password:  <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }


}







export default JobTrackerApp
