import React, {Component} from 'react'

class JobTrackerApp extends Component {
    render(){
        return (
            <div className="JobTrackerApp">
                My Job Tracker Application
                <LoginComponent/>
            </div>
        )
    }
}

class LoginComponent extends Component{

    constructor(props){
        super(props)
        this.state = {
            username: 'jobTracker',
            password: ''
        }

        this.handleUsernameChange = this.handleUsernameChange.bind(this)
    }

    handleUsernameChange(event) {
        console.log(event.target.value);
    }

    render(){
        return (
            <div>
            UserName: <input type="text" name="username" value= {this.state.username} onChange={this.handleUsernameChange} />
            Password:  <input type="password" name="password" value={this.state.password} />
            <button>Login</button>
            </div>
        )
    }

}

export default JobTrackerApp
