import React, {Component} from 'react'

class JobTrackerApp extends Component {
    render(){
        return (
            <div className="JobTrackerApp">
                My Job Tracker Application
                <LoginComponent/>
            </div>
        )
    }
}

class LoginComponent extends Component{

    constructor(props){
        super(props)
        this.state = {
            username: 'jobTracker',
            password: '',
            loginStatus: false,
            loginMessage: false
        }


        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    handleChange(event) {
        this.setState({
            [event.target.name]
            :event.target.value
        })
    }

    loginClicked(){
        if(this.state.username==='admin' && this.state.password === 'password'){
            console.log('Successful')
            this.setState({
                loginMessage:true
            })
            this.setState({ loginStatus:true

            })
        }
        else
            console.log('Failed')
            this.setState({
                loginMessage:false
            })
            this.setState({loginStatus:false})
    }

    render(){
        return (

            <div>   
                <ShowInvalidCredentials hasSuccessfulLogin = {this.state.login} />
                UserName: <input type="text" name="username" value= {this.state.username} onChange={this.handleChange} />
                Password:  <input type="password" name="password" value={this.state.password} onChange={this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }
}

function showLoginSuccessful(props){
    if(props.loginStatus){
        return <div>Login Successful</div>
    }
    return null
}

function  ShowInvalidCredentials(props) {
    if(props.loginStatus === false)  {          
        return  <div>Invalid Credentials, Please Try Again</div>
    }
    return null
}


export default JobTrackerApp
