import React, { Component } from "react";
import JobsDataService from "../../api/job/JobsDataService.js";
import AuthenticationService from "./AuthenticationService.js";
import moment from "moment";

class ListJobsComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      message : null
    };

    this.deleteJobClicked = this.deleteJobClicked.bind(this);
  }

  componentDidMount() {
    let username = AuthenticationService.getLoggedInUserName();
    JobsDataService.retrieveAllJobs(username).then((response) => {

      this.setState({ jobs: response.data });
    });
    console.log
  }

  deleteJobClicked(id) {
    let username = AuthenticationService.getLoggedInUserName();
    JobsDataService.deleteJob(username, id).then(response => {
     this.setState({message:`deletion of job ${id} Successful`})
    })
  }

  render() {
    return (
      <div>
        <h1>Jobs</h1>
    {this.state.message && <div className= "alert alert-success">{this.state.message}</div>}

        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th>Job Title</th>
                <th>Company</th>
                <th>Link</th>
                <th>Status</th>
                <th>Date</th>
                <th>Notes</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {this.state.jobs.map((job) => (
                <tr key={job.id}>
                  <td>{job.jobTitle}</td>
                  <td>{job.company}</td>
                  <td>{job.webLink}</td>
                  <td>{job.status}</td>
                  <td>{moment(job.appliedDate).format("MM DD, YYYY")}</td>
                  <td>{job.notes}</td>
                  <td>
                    <button
                      className="btn btn-warning"
                      onClick={() => this.deleteJobClicked(job.id)}
                    >
                      Delete
                    </button>
                  </td>
                  <td></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListJobsComponent;
